######## training for dpo using reverse_kl but sft is done on hh and shp. ########
ulimit -n 64000; python -u train.py model=pythia28 datasets=[hh] loss=dpo fsdp_port=8888 loss.beta=0.9 exp_name=anthropic_dpo_reverse_kl_pythia28_hh0.9 gradient_accumulation_steps=2 batch_size=64 eval_batch_size=32 trainer=FSDPTrainer sample_during_eval=false model.fsdp_policy_mp=bfloat16